import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'; // Keep Bootstrap CSS import here

import Container from 'react-bootstrap/Container'; // Keep Container for main content

// Import your new Header and Footer components
import Header from './components/Header';
import Footer from './components/Footer';

function App() {
  return (
    <div className="App">
      {/* Use the Header component */}
      <Header />

      {/* Main content area */}
      <Container className="mt-4">
        {/*
          This is where the components for your different pages (Login, Dashboard, etc.)
          will be rendered based on the routing setup you will add later.
          For now, you can keep placeholder content.
        */}
        <h2>Welcome to the SimAcc Portal Frontend</h2>
        <p>Start building your page components here.</p>
      </Container>

      {/* Use the Footer component */}
      <Footer />
    </div>
  );
}

export default App;
