import React, { useState } from 'react';
// Import Bootstrap components
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

// Import the reusable feedback components
import ErrorMessage from '../components/ErrorMessage';
import SuccessMessage from '../components/SuccessMessage';
import LoadingSpinner from '../components/LoadingSpinner';

// Import useNavigate from react-router-dom for redirection
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  // --- State Variables ---
  // State for input fields
  const [accountNumber, setAccountNumber] = useState('');
  // We no longer need 'password' state for this flow
  const [otp, setOtp] = useState(''); // State for the entered OTP

  // State to track the stage of the login process
  const [otpSent, setOtpSent] = useState(false); // True after OTP request is successful

  // State for feedback (loading, error, success)
  const [isLoading, setIsLoading] = useState(false); // Controls spinner and form visibility
  const [error, setError] = useState(null); // Holds error message text
  const [successMessage, setSuccessMessage] = useState(null); // Holds success message text

  // --- React Router Hook ---
  // Get the navigate function for redirection
  const navigate = useNavigate();

  // --- Input Change Handlers ---
  // Updates state when account number input changes
  const handleAccountNumberChange = (event) => {
    setAccountNumber(event.target.value);
    // Optionally clear feedback when input changes
    setError(null);
    setSuccessMessage(null);
  };

  // Updates state when OTP input changes
  const handleOtpChange = (event) => {
    setOtp(event.target.value);
    // Optionally clear feedback when input changes
    setError(null);
    setSuccessMessage(null);
  };

  // --- Form Submission Handler ---
  // This function handles submission for BOTH stages
  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent the default browser page reload

    setIsLoading(true); // Start loading for the current stage
    setError(null); // Clear previous errors
    setSuccessMessage(null); // Clear previous success messages

    try {
      if (!otpSent) { // --- STAGE 1: REQUEST OTP ---
        console.log(`Requesting OTP for account number: ${accountNumber}`);
        // *** Simulate an API call to check account and send OTP ***
        await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate network delay

        // *** Simulate API response for OTP request ***
        // For simulation, let's say OTP is sent if the account number is '0786399754' or 'Colleen'
        let apiResponse;
        if (accountNumber === '0786399754' || accountNumber.toLowerCase() === 'colleen') { // Case-insensitive check for 'colleen'
           apiResponse = { success: true, message: 'OTP sent to your registered phone/email!' };
           // In a real app, the backend would actually send the OTP here
        } else {
           apiResponse = { success: false, message: 'Account number not found.' };
        }
        // *** End of simulation for OTP request ***

        if (apiResponse.success) {
          setSuccessMessage(apiResponse.message);
          setOtpSent(true); // Move to the OTP verification stage
        } else {
          setError(apiResponse.message);
          // Stay on the same stage, prompt user to re-enter account number
        }

      } else { // --- STAGE 2: VERIFY OTP ---
        console.log(`Verifying OTP for account ${accountNumber}: ${otp}`);
        // *** Simulate an API call to verify the entered OTP ***
        await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate network delay

        // *** Simulate API response for OTP verification ***
        // For simulation, let's say the correct OTP is '1234'
        let apiResponse;
        if (otp === '1234') {
          apiResponse = { success: true, message: 'OTP verified! Logging in...' };
          // In a real app, the backend confirms the OTP and returns authentication token/status
        } else {
          apiResponse = { success: false, message: 'Invalid OTP. Please try again.' };
        }
        // *** End of simulation for OTP verification ***

        if (apiResponse.success) {
          setSuccessMessage(apiResponse.message);
          setError(null); // Clear any previous error
          // *** Redirect to the dashboard page upon successful OTP verification ***
          // You would typically save the user's authentication token here
          navigate('/dashboard'); // Use the navigate function to change the route

        } else {
          setError(apiResponse.message);
          // Stay on the OTP verification stage, prompt user to re-enter OTP
        }
      }

    } catch (err) {
      // Handle unexpected errors during API call simulation
      console.error('Simulation Error:', err);
      setError('An unexpected error occurred. Please try again.');
      setSuccessMessage(null); // Clear success message on error
    } finally {
      setIsLoading(false); // Stop loading regardless of outcome
    }
  };

  // --- Component JSX (What gets rendered) ---
  return (
    // Outer Container to center the card
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
      {/* The Card component */}
      <Card className="p-4 shadow-lg rounded" style={{ width: '25rem' }}>
        <Card.Body>
          {/* --- Feedback (Spinner, Error, Success) --- */}
          {/* Show spinner when isLoading is true */}
          {isLoading && <LoadingSpinner />}
          {/* Show error message when there is an error */}
          {error && <ErrorMessage message={error} />}
          {/* Show success message when there is a success message */}
          {successMessage && <SuccessMessage message={successMessage} />}

          {/* --- Form Content (Conditional based on stage) --- */}
          {/* Show the appropriate form only when not loading */}
          {!isLoading && (
            <> {/* Use a React Fragment to group multiple elements */}
              {/* Card Title - Adjust based on stage */}
              <Card.Title className="text-center mb-4">
                {otpSent ? 'Verify OTP' : 'SimAcc Portal Login'} {/* Title changes based on stage */}
              </Card.Title>

              {/* The Form */}
              <Form onSubmit={handleSubmit}>

                {!otpSent ? ( // --- Render Account Number form if OTP not sent ---
                  <>
                    <Form.Group className="mb-3" controlId="formBasicAccountNumber">
                      <Form.Label>Account Number</Form.Label> {/* Updated label */}
                      <Form.Control
                        type="text"
                        placeholder="Enter your account number"
                        value={accountNumber}
                        onChange={handleAccountNumberChange}
                        required
                        disabled={isLoading}
                      />
                    </Form.Group>

                    {/* No Password field in this flow */}

                    {/* Request OTP Button */}
                    <Button
                      variant="primary"
                      type="submit"
                      className="w-100 mt-3"
                      disabled={isLoading}
                    >
                      Request OTP {/* Updated button text */}
                    </Button>
                  </>
                ) : ( // --- Render OTP verification form if OTP sent ---
                  <>
                     <p className="text-muted text-center mb-3">
                        An OTP has been sent to your registered phone number or email.
                     </p>
                    <Form.Group className="mb-3" controlId="formBasicOtp"> {/* New controlId */}
                      <Form.Label>Enter OTP</Form.Label> {/* New label */}
                      <Form.Control
                        type="text" // OTP is often text/number
                        placeholder="Enter the OTP" 
                        value={otp}
                        onChange={handleOtpChange} // Use the new handler
                        required
                        disabled={isLoading}
                      />
                    </Form.Group>

                    {/* Verify OTP Button */}
                    <Button
                      variant="success" // Different color for clarity
                      type="submit"
                      className="w-100 mt-3"
                      disabled={isLoading}
                    >
                      Verify OTP {/* New button text */}
                    </Button>

                    {/* Option to Resend OTP (Optional) */}
                     <div className="text-center mt-3">
                         <a href="#" onClick={(e) => {
                             e.preventDefault();
                             // Implement resend OTP logic here
                             console.log('Resend OTP clicked');
                             // You'd likely call a backend API here and maybe reset the timer
                             setSuccessMessage('Resending OTP...'); // Optional feedback
                             setError(null);
                             setIsLoading(true); // Show spinner during resend
                             // Simulate resend delay
                             setTimeout(() => {
                                 setSuccessMessage('OTP resent!');
                                 setIsLoading(false);
                             }, 1000);

                         }} className="text-primary text-decoration-none">
                             Resend OTP
                         </a>
                     </div>
                  </>
                )}

                {/* Link for Account Creation (remains at the bottom) */}
                <div className="text-center mt-3">
                  <p className="text-muted mb-1">{!otpSent ? "Don't have an account?" : "Need help logging in?"}</p> {/* Text changes based on stage */}
                  {/* Link to the registration page or help page */}
                  <a href="/register" className="text-primary text-decoration-none">
                    {!otpSent ? "Create Account" : "Contact Support"} {/* Text changes based on stage */}
                  </a>
                </div>

              </Form>
            </>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
}

export default LoginPage;
